// integration of BootstrapVue with a Vue.js application.
// imports the necessary packages and styles, including the core Bootstrap CSS and the BootstrapVue-specific CSS
import Vue from 'vue';
import { BootstrapVue } from 'bootstrap-vue';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-vue/dist/bootstrap-vue.css';

export default Vue.use(BootstrapVue);
